import warning from '../../../assets/images/Icons/error-24px.svg'



import React, { Component } from 'react'

export default class Input extends Component {
    constructor(props){
        super(props)
        this.state = {
            empty: props.empty ? 'form__input--empty' : '',
            invalidEmail: false,
            emptyWarning: 'This field is required',
            emailWarning: 'Please provide a valid email address'
        }
    }

    validateEmail(email) {
        const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return re.test(String(email).toLowerCase());
    }

    handleChange(e){
        props.handleChange(e)
        if(this.props.name === 'email' && !this.validateEmail(e.target.value)){
            this.setState({
                ...this.state
            })
        } 
    }

    render() {
        return (
            <div className="form__input-wrapper">
                {props.empty}
                <label htmlFor={props.name} className="form__label">{props.text}</label>
                <input onChange={(e)=>{props.handleChange(e)}} name={props.name} type="text" className={`form__input ${empty}`}/>
                {props.empty && 
                    <span className="form__validation-span">
                        <img src={warning} alt="" className="form__warning"/>
                        This field is required
                    </span>
                }
            </div>
        )
    }
}
