import React from 'react'

export default function Input(props) {
    return (
        <div className="form__input-wrapper">
            
            <label htmlFor={props.name} className="form__label">{props.text}</label>
            <input onChange={(e)=>{props.handleChange(e)}} name={props.name} type="text" className='form__input'/>
            {props.empty && <span className="form__validation">This field is required</span>}
        </div>
    )
}
