import React, { Component } from 'react'
import arrow from '../../assets/images/Icons/arrow_back-24px.svg'
import axios from'axios'
import Input from './Input/Input'
import './AddWarehouse.scss'

export default class AddWarehouse extends Component {
    constructor(props){
        super()
        this.state = {
            name: '',
            address:'',
            city: '',
            country: '',
            contact: {
              name: '',
              position: '',
              phone: '',
              email: ''
            }
        }
    }

    handleDetailChange(e){
        this.setState({
            ...this.state,
            [e.target.name]: e.target.value
        })

        console.log(this.state[e.target.name]);
    }

    handleContactChange(e){
        this.setState({
            ...this.state,
            contact: {
                ...this.state.contact,
                [e.target.name]: e.target.value
            }
        })

        console.log(this.state.contact[e.target.name]);
    }

    handleSubmit(e){
        e.preventDefault()
    }

    render() {
        return (
            <div className = 'form'>

                {/* header */}
                <div className="form__header">
                    <img src={arrow} className = 'form__back-arrow' alt=""/> 
                    <h1 className="form__heading titilliumWeb-Regular">Add New Warehouse</h1>                    
                </div>

                {/* flex container */}
                <div className="flex-container">

                    {/* details section */}
                    <div className="form__left-section">
                        <h2 className="form__subheading">Warehouse Details</h2>

                        <Input name='name' text={'Warehouse Name'} handleChange={(e)=>this.handleDetailChange(e)}/>
                        <Input name='address' text={'Street Address'} handleChange={(e)=>this.handleDetailChange(e)}/>
                        <Input name='city' text={'City'} handleChange={(e)=>this.handleDetailChange(e)}/>


                    {/* contact */}
                    <div className="form__right-section">
                    <h2 className="form__subheading">Contact</h2>

                        <div className="form__input-wrapper">
                            <label htmlFor="name" className="form__label">Contact Name</label>
                            <input type="text" onChange={(e)=>this.handleContactChange(e)} name='name' className='form__input'/>
                        </div>

                        <div className="form__input-wrapper">
                            <label htmlFor="position" className="form__label">Position</label>
                            <input type="text" onChange={(e)=>this.handleContactChange(e)} name='position' className='form__input'/>
                        </div>

                        <div className="form__input-wrapper">
                            <label htmlFor="phone" className="form__label">Phone number</label>
                            <input type="text" onChange={(e)=>this.handleContactChange(e)} name='phone' className='form__input'/>
                        </div>

                        <div className="form__input-wrapper">
                            <label htmlFor="email" className="form__label">Email</label>
                            <input type="text" onChange={(e)=>this.handleContactChange(e)} name='email' className='form__input'/>
                        </div>
                    </div>
                </div>
                <div className="form__button-container">
                    <button className="form__button">Cancel</button>
                    <button className="form__button">Add Warehouse</button>
                </div>
            </div>
            </div>
        )
    }
}
